y_gap = 0

for y = 0, 27 do
    for x = 0, 30 do
        ex3.draw_static_sprite(map[x+y*31], 0, x+x_gap, y+y_gap)
    end
end

function check_walkable(x, y)
    x = (x - 8) / 16 - x_gap
    y = (y - 8) / 16 - y_gap
    
    chip = map[x+y*31]
    __ASM__("BREAK")
    return 1
end

pacman_x = 8 + 16 * (x_gap + 0)
pacman_y = 8 + 16 * (y_gap + 0)
pacman_rot = 0
pacman_anim = 0

while 1 do
    -- アニメーション
    pacman_anim = pacman_anim + 1
    if pacman_anim > 2 then
        pacman_anim = 0
    end
    
    -- キー状態を確認
    key_r, key_u, key_d, key_l = ex3.get_key_state()
    
    if key_r then
        pacman_rot = 2
        pacman_x = pacman_x + 2
    elseif key_u then
        pacman_rot = 3
        pacman_y = pacman_y - 2
    elseif key_d then
        pacman_rot = 1
        pacman_y = pacman_y + 2
    elseif key_l then
        pacman_rot = 0
        pacman_x = pacman_x - 2
    end
    
    -- パックマン描画
    ex3.draw_dynamic_sprite(8*2+pacman_anim, pacman_rot, pacman_x, pacman_y, 0)
    
    ex3.sleep()
end
